import React, { useState } from 'react';

//figure out how to send copy of safety plan to user's email
function SafetyPlanForm(){
    const [formData, setFormData] = useState({
        question1: '',
        question2: '',
        question3: '',
        question4: '',
        question5: '',
    });

    function handleChange(event) {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({
        ...prevFormData,
            [name]: value,
        }));
        }

    function handleSubmit(event) {
        event.preventDefault();

        const{question1, question2, question3, question4, question5}=formData;
//turn form data into an object
        const data ={
            question1: question1,
            question2: question2,
            question3: question3,
            question4: question4,
            question5: question5,
        };

//Post request to go to my backend
fetch('/generate_safety_plan', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json',
    },
    body: JSON.stringify(formData),
})
    .then((response) => response.json())
    .then((data) => {
    console.log(data);
    });
}

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        Question 1:
                        <input
                            type="text"
                            name="question1"
                            value={formData.question1}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <div>
                    <label>
                        Question 2:
                        <input
                            type="text"
                            name="question2"
                            value={formData.question2}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <div>
                    <label>
                        Question 3:
                        <input
                            type="text"
                            name="question3"
                            value={formData.question3}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <div>
                    <label>
                        Question 4:
                        <input
                            type="text"
                            name="question4"
                            value={formData.question4}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <div>
                    <label>
                        Question 5:
                        <input
                            type="text"
                            name="question5"
                            value={formData.question5}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <button type="submit">Generate Safety Plan</button>
            </form>
        </div>
    );
};

export default SafetyPlanForm;
