import React, { useState } from "react";

function ConsultForm() {
    const [value, setValue] = useState(" ");
    const [formSubmitted, setFormSubmitted] = useState(false);

    const handleChange = (event) => {
        setValue(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        //topic selection validation
        if (value === "") {
            alert("Please select a topic before submitting.");
            return;
        }

        const data = { topic: value };
        fetch('/api/consultations', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
        });

        // Set formSubmitted to true to display a success message or redirect the user to a confirmation page
        setFormSubmitted(true);
    };

    return (
        <div>
            {formSubmitted ? (
                <p>Thank you for submitting the form! We will get in touch with you soon.</p>
            ) : (
                <form onSubmit={handleSubmit}>
                    <label>
                        Select A Topic You Need Help In:
                        <select value={value} onChange={handleChange}>
                            <option value="">-- Select a topic --</option>
                            <option value="legal">Legal</option>
                            <option value="food">Food</option>
                            <option value="health">Health</option>
                            <option value="housing">Housing</option>
                        </select>
                    </label>
                    <label>Name: </label>
                    <input type="submit" value="Submit" />
                </form>
            )}
        </div>
    );
}

export default ConsultForm;
